        and cate_dynamic_url = #{param.cateDynamicUrl}


        and cate_name = #{param.cateName}


        and cate_pic_path = #{param.catePicPath}


        and cate_satic_url = #{param.cateSaticUrl}


        and cate_type = #{param.cateType}


        and com_id = #{param.comId}


        and create_dt = #{param.createDt}


        and create_uid = #{param.createUid}


        and data_flg = #{param.dataFlg}


        and flow_status = #{param.flowStatus}


        and info_jsp_template = #{param.infoJspTemplate}


        and is_auth_control = #{param.isAuthControl}


        and is_https = #{param.isHttps}


        and is_info_container = #{param.isInfoContainer}


        and is_in_sitemap = #{param.isInSitemap}


        and is_lucene_search = #{param.isLuceneSearch}


        and is_navigator_menu = #{param.isNavigatorMenu}


        and is_prepub_jms = #{param.isPrepubJms}


        and is_pub_jms = #{param.isPubJms}


        and is_pub_rss = #{param.isPubRss}


