mapper.select:
select *  from portal_dynamic_pic


        and com_id = #{param.comId}


        and create_dt = #{param.createDt}


        and create_uid = #{param.createUid}


        and data_flg = #{param.dataFlg}


        and flow_status = #{param.flowStatus}


        and flow_trans_id = #{param.flowTransId}


        and is_mobile = #{param.isMobile}


        and is_mobile_adv = #{param.isMobileAdv}


        and master_row_id = #{param.masterRowId}


        and mobile_url = #{param.mobileUrl}


        and obj_name = #{param.objName}


        and pic_title = #{param.picTitle}


        and pic_url = #{param.picUrl}


        and pic_url2 = #{param.picUrl2}


        and pic_url_link = #{param.picUrlLink}


        and pig_group_id = #{param.pigGroupId}


        and pk_id = #{param.pkId}


        and publish_dt = #{param.publishDt}


        and row_id = #{param.rowId}


        and sort_no = #{param.sortNo}


        and store_id = #{param.storeId}


        and update_dt = #{param.updateDt}


        and update_uid = #{param.updateUid}


        and website_code = #{param.websiteCode}

