mapper.select:
select *  from comm_auth


        and always_show = #{param.alwaysShow}


        and cls_code = #{param.clsCode}


        and data_flg = #{param.dataFlg}


        and is_assign_to_com = #{param.isAssignToCom}


        and is_layui = #{param.isLayui}


        and is_vue = #{param.isVue}


        and layui_jump = #{param.layuiJump}


        and layui_name = #{param.layuiName}


        and menu_sort_no = #{param.menuSortNo}


        and menu_url = #{param.menuUrl}


        and new_url = #{param.newUrl}


        and no_keep_alive = #{param.noKeepAlive}


        and pic_file = #{param.picFile}


        and sys_code = #{param.sysCode}


        and sys_role = #{param.sysRole}


        and vue_component = #{param.vueComponent}


        and vue_hidden = #{param.vueHidden}


        and vue_icon = #{param.vueIcon}


        and vue_path = #{param.vuePath}


        and vue_redirect = #{param.vueRedirect}

select *  from comm_auth

