        and is_sort_asc = #{param.isSortAsc}


        and sort_col_num = #{param.sortColNum}


        and data_get_type = #{param.dataGetType}


        and date_format_type = #{param.dateFormatType}


        and input_type = #{param.inputType}


        and js_pop_windows = #{param.jsPopWindows}


