select *  from sms_ent_account


        and balance_amt = #{param.balanceAmt}


        and create_dt = #{param.createDt}


        and create_uid = #{param.createUid}


        and data_flg = #{param.dataFlg}


        and end_name = #{param.endName}


        and ent_code = #{param.entCode}


        and ext_code = #{param.extCode}


        and flow_status = #{param.flowStatus}


        and flow_trans_id = #{param.flowTransId}


        and is_default = #{param.isDefault}


        and master_row_id = #{param.masterRowId}


        and obj_name = #{param.objName}


        and proxy_url = #{param.proxyUrl}


        and record_version = #{param.recordVersion}


        and row_id = #{param.rowId}


        and sign_words = #{param.signWords}


        and sms_sup_code = #{param.smsSupCode}


        and sms_type = #{param.smsType}


        and sms_userid = #{param.smsUserid}


        and sort_no = #{param.sortNo}


        and st_code = #{param.stCode}


        and update_dt = #{param.updateDt}


        and update_uid = #{param.updateUid}


        and user_cn_name = #{param.userCnName}


        and user_name = #{param.userName}


        and user_password = #{param.userPassword}

